{
    if( this==&rhs )
        return *this;
    alglib_impl::_logitmodel_clear(p_struct);
    alglib_impl::_logitmodel_init_copy(p_struct, const_cast<alglib_impl::logitmodel*>(rhs.p_struct), NULL);
    return *this;
}

